<?phpif (!class_exists('UpiCRMAdminAffiliate')):    class UpiCRMAdminAffiliate {            public function __construct() {                    }        public function Render() {            $UpiCRMFields = new UpiCRMFields();            $UpiCRMUIBuilder = new UpiCRMUIBuilder();            if (isset($_POST['action'])) {                switch ($_POST['action']) {                    case 'save_field':                        $this->saveField();                        $msg = __('changes saved successfully', 'upicrm');                        break;                }            }            ?>            <div class="row">                <?php                if (isset($msg)) {                    ?>                    <div class="updated">                        <p><?php echo $msg; ?></p>                    </div>                    <?php                }                ?>                                </div>                <h2><?php _e('Use shortcode', 'upicrm'); ?></h2>                [upicrm_affiliate]                <br/><strong><?php _e('OR', 'upicrm'); ?></strong><br/>                [upicrm_affiliate field="ID" show="month/no_month"]                <br /><br />                <?php                require_once @get_upicrm_template_path('affiliates form');            }            function saveField() {                update_option('upicrm_affiliate_show_fields',$_POST['field']);            }        }  endif;